import React from 'react';
import KeyboardEventHandler from 'react-keyboard-event-handler'
import _ from 'lodash'

class Snake extends React.Component{
    selfCollide() {
      const snake = this.props.snake;
      return snake.body.some(cord => _.isEqual(cord, snake.head)) || _.isEqual(snake.head, snake.tail)
    }
    run() {
        this.props.snake.running = true;
        var running = setInterval(() => {
            const snake = this.props.snake;
            switch(snake.direction){
                case 'up':
                  snake.head.y -= 1;
                  break;
                case 'down':
                  snake.head.y += 1;
                  break;
                    case 'left':
                snake.head.x -= 1;
                  break;
                case 'right':
                  snake.head.x += 1;
                  break;
                default:
                break;
            }

            if(this.props.snake.running === false){
                clearInterval(running)
            }

            if (snake.head.x > 29 || snake.head.y > 29 || snake.head.x < 0 || snake.head.y < 0 || this.selfCollide()) {
              snake.running = false
              snake.alive = false
              clearInterval(running);
            }

            var newCell = {
              x: snake.head.x,
              y: snake.head.y
            }

            snake.body.push(newCell)
            snake.tail.x = snake.body[0].x;
            snake.tail.y = snake.body[0].y;
            snake.body.splice(0,1);

            this.props.changeDirection(snake.direction);

        }, 200 / this.props.snake.speed);
      }
      endGame() {
        const {ctx} = this.state

        // let newState = Object.assign({}, this.state);
        // newState.snake.running = false;
        // newState.snake.alive = false;
        // this.setState(newState);

        //Horizonal Lines
        ctx.fillStyle = 'white';
        this.drawRect(5,9,4,1);
        this.drawRect(5,13,4,1);
        this.drawRect(11,9,2,1);
        this.drawRect(11,12,2,1);
        this.drawRect(22,9,3,1);
        this.drawRect(22,11,3,1);
        this.drawRect(22,13,3,1);
        this.drawRect(6,16,2,1);
        this.drawRect(6,20,2,1);
        this.drawRect(16,16,4,1);
        this.drawRect(16,18,4,1);
        this.drawRect(16,20,4,1);
        this.drawRect(21,16,4,1);
        this.drawRect(21,18,4,1);
        this.drawRect(23,20,2,1);

        //Vertical Lines
        this.drawRect(5,10,1,3);
        this.drawRect(10,10,1,4);
        this.drawRect(13,10,1,4);
        this.drawRect(15,9,1,5);
        this.drawRect(19,9,1,5);
        this.drawRect(21,9,1,5);
        this.drawRect(5,17,1,3);
        this.drawRect(8,17,1,3);
        this.drawRect(10,16,1,3);
        this.drawRect(14,16,1,3);
        this.drawRect(16,16,1,5);
        this.drawRect(21,16,1,5);
        this.drawRect(24,16,1,3);

        //Dots
        this.drawRect(7,11,1,1);
        this.drawRect(8,12,1,1);
        this.drawRect(16,10,1,1);
        this.drawRect(17,11,1,1);
        this.drawRect(18,10,1,1);
        this.drawRect(11,19,1,1);
        this.drawRect(12,20,1,1);
        this.drawRect(13,19,1,1);
        this.drawRect(22,19,1,1);
      }



    render(){
       return(
           <div id="Snake">
            <KeyboardEventHandler
                  handleKeys={['left', 'up', 'right', 'down', 'space']}
                  onKeyEvent={(key, e) => {
                    if (key === 'up' && (this.props.snake.direction === 'down' || this.props.snake.direction === 'up')) {
                      return
                    }
                    if (key === 'down' && (this.props.snake.direction === 'down' || this.props.snake.direction === 'up')) {
                      return
                    }
                    if (key === 'left' && (this.props.snake.direction === 'left' || this.props.snake.direction === 'right')) {
                      return
                    }
                    if (key === 'right' && (this.props.snake.direction === 'left' || this.props.snake.direction === 'right')) {
                      return
                    }
                    if (this.props.snake.alive === false && this.props.snake.running === false){
                      this.endGame();
                    }
                    if (!this.props.snake.running && this.props.snake.alive ){
                        this.run();
                    }
                    this.props.changeDirection(key);
                  }} />   
           </div>
       );
   }
}

export default Snake;
